select r.rest_id, i.rest_name, i.food_type, i.favorites, i.address, round(sum(r.review_score) / count(r.rest_id), 2) as 'average' from rest_info i inner join rest_review r on i.rest_id = r.rest_id where left(i.address, 2) = '서울' group by i.rest_id order by average desc, i.favorites desc